import React from 'react'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent';
import TextField from '@mui/material/TextField';
import {Person} from "@mui/icons-material";
import {useState, useEffect} from "react";
import api from '../api/api.ts'

export default function FormularioLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState({ email: '', password: '' });
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleEmailChange = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setEmail(event.target.value);
    };

    const handlePasswordChange = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setPassword(event.target.value);
    };

    const validate = () => {
        let isValid = true;
        let errors = { email: '', password: '' };

        if (!email) {
            errors.email = 'El correo es obligatorio';
            isValid = false;
        } else if (!/\S+@\S+\.\S+/.test(email)) {
            errors.email = 'El correo no es válido';
            isValid = false;
        }

        if (!password) {
            errors.password = 'La contraseña es obligatoria';
            isValid = false;
        }

        setErrors(errors);
        return isValid;
    };

    const handleSubmit = async (event: { preventDefault: () => void; }) => {
        event.preventDefault();
        if (validate()) {
            setIsSubmitting(true);

            try {
                const response = await api.post('/rest/v1/login', { email, password });
                console.log('Respuesta de la API:', response.data);
            } catch (error) {
                console.error('Error en la solicitud:', error);
            } finally {
                setIsSubmitting(false);
            }
        }
    };

    return (
        <form on>
           <Card>
               <CardContent>
                   <div className="flex min-h-full flex-col justify-center px-6 py-12 lg:px-8">
                       <div className="sm:mx-auto sm:w-full sm:max-w-sm">
                           <div className='flex items-center justify-center'>
                               <Person style={{fontSize:'4rem'}}/>
                           </div>
                           <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
                               Inicia sesión con tu cuenta
                           </h2>
                       </div>

                       <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
                           <form className="space-y-6" action="#" method="POST">
                               <div>
                                   <label htmlFor="email" className="block text-sm font-medium leading-6 text-gray-900">Ingrese su correo</label>
                                   <div className="mt-2">
                                       <TextField
                                            id="email"
                                            variant="standard"
                                            className='w-full'
                                            value={email} // Enlazar el valor del input con el estado
                                            onChange={handleEmailChange} // Actualizar el estado cuando cambia el input
                                        />
                                   </div>
                               </div>

                               <div>
                                   <div className="flex items-center justify-between">
                                       <label htmlFor="password"
                                              className="block text-sm font-medium leading-6 text-gray-900">Ingrese su contraseña</label>
                                   </div>
                                   <div className="mt-2">
                                       <TextField
                                            id="password"
                                            type="password"
                                            variant="standard"
                                            className='w-full'
                                            value={password} // Enlazar el valor del input con el estado
                                            onChange={handlePasswordChange} // Actualizar el estado cuando cambia el input
                                        />
                                   </div>
                               </div>

                               <div>
                                   <button type="submit"
                                           className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5
                                           text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500
                                           focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2
                                           focus-visible:outline-indigo-600">
                                       Ingresar
                                   </button>
                               </div>
                           </form>

                       </div>
                   </div>

               </CardContent>
           </Card>
        </form>
    )
}
