import axios from 'axios'


const api = axios.create({
    baseURL: 'https://127.0.0.1:8000/',
})

export const login = async (usuario:string, password:string) => {
    try{
        const peticion = {
            usuario: usuario,
            password: password,
        }
        return await api.post('/rest/v1/login', {
            body: JSON.stringify(peticion),
        })

    }catch (error){
        console.log(error)
        throw error;
    }

}
export default api;
